<script>
	import { afterNavigate, goto } from '$app/navigation';
	import { page } from '$app/stores';
	import { onMount, setContext } from 'svelte';
	import { writable } from 'svelte/store';
	import '../app.pcss';
	import './styles.css';
	import mainLogo from '$lib/images/mainlogo.png';
	const navSlide = writable();
	setContext('navSlide', navSlide);

	const hotspotName = writable(0, () => {
		return () => console.log('remove sub');
	});
	const UIPanel = writable();
	$: UIPanel.set('loading');
	const walkthroughDisabled = writable();
	$: walkthroughDisabled.set(false);

	$: hotspotName.set('Exterior');
	setContext('hotspotName', hotspotName);
	setContext('UIPanel', UIPanel);

	const show = (ui) => {
		$currentUI = {
			interiors: false,
			amenities: false,
			interiorsDollhouse: false,
			highlights: false
		};
		$currentUI[ui] = true;
		console.log('second', $currentUI);
	};
	const vicinityImg = writable();
	$: vicinityImg.set('-');
	setContext('vicinityImg', vicinityImg);

	// check for the iframe window
	function inIframe() {
		try {
			return window.self !== window.top;
		} catch (e) {
			return true;
		}
	}
	let isIframe = inIframe();

	let currentUI = writable({
		Exterior: true,
		interiors: false,
		amenities: false,
		highlights: false
	});

	setContext('currentUI', currentUI);
	const switchChecker = () => {
		// eslint-disable-next-line svelte/valid-compile
		switch ($page.url.searchParams.get('panel')) {
			case 'interiors':
				show('interiors');
				UIPanel.set('loaded');
				break;
			case 'contact-us':
				show('contact');
				UIPanel.set('contact');
				break;
			case 'brochure':
				show('Brochure');
				UIPanel.set('Brochure');
				break;
			case 'amenities':
				show('amenities');
				UIPanel.set('loaded');
				break;
			case 'vicinity':
				show('vicinity');
				UIPanel.set('loaded');
				break;
			case 'overview':
				show('Exterior');
				UIPanel.set('loaded');
				break;
			case 'highlights':
				show('highlights');
				UIPanel.set('loaded');
				break;
			default:
				break;
		}
	};
	onMount(() => {
		switchChecker();
		console.log('page changed', $page.url);
		if ($page.url.href.includes('brochure')) {
			show('Brochure');
			UIPanel.set('Brochure');
		}
		if ($page.url.href.includes('amenities')) {
			show('amenities');
			UIPanel.set('loaded');
		}
		// if ($page.url.href.includes('localhost') || true) return;
		const script = document.createElement('script');
		script.id = 'vretail-script';
		script.setAttribute('data-vretail-project-id', import.meta.env.VITE_PROJECT_ID);
		script.src = import.meta.env.VITE_COLLAB_URL;
		document.body.insertAdjacentElement('beforeend', script);
	});

	afterNavigate(() => {
		const isIOS = /iPad|iPhone|iPod/.test(navigator.userAgent) && !window.MSStream;
		if (!isIOS) return;

		setTimeout(() => {
			window.scrollTo(0, 1);
			setTimeout(() => window.scrollTo(0, 0), 100);

			['.nav-wrapper', '.left-panel-wrapper', '.color-mode-wrapper'].forEach((sel) => {
				const el = document.querySelector(sel);
				if (el) el.style.position = 'fixed';
			});
		}, 400);
	});
</script>

